from datetime import datetime
import matplotlib.pyplot as plt
import pandas as pd

mk_repost_list =[[datetime.strptime(repost[0][1],'%Y/%m/%d'),repost[1]] for repost in repost_list if repost[0][0]=='mk']
ks_repost_list = [[datetime.strptime(repost[0][1],'%Y/%m/%d'),repost[1]] for repost in repost_list if repost[0][0]=='ks']

mk_comment_list =[[datetime.strptime(comment[0][1],'%Y/%m/%d'),comment[1]] for comment in comment_list if comment[0][0]=='mk']
ks_comment_list = [[datetime.strptime(comment[0][1],'%Y/%m/%d'),comment[1]] for comment in comment_list if comment[0][0]=='ks']


